components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1080px;
  font-family: 'Inter', Arial, sans-serif;
  background: ${bg_color};
}
.poster {
  width: 1080px;
  height: 1080px;
  padding: 80px;
  background: ${bg_color};
  position: relative;
  overflow: hidden;
}
.header {
  display: flex;
  align-items: center;
  gap: 15px;
}
.header img {
  height: 30px;
  object-fit: contain;
}
.content {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 90%;
  transform: translate(-50%, -50%);
  display: flex;
  justify-content: center;
  gap: 100px;
  align-items: center;
}
.phone {
  position: relative;
}
.phone-mockup {
  height: 750px;
  display: block;
}
.phone-screen {
  position: absolute;
  top: 40px;
  left: 50px;
  width: 320px;
  height: 670px;
  border-radius: 40px;
  overflow: hidden;
  box-shadow: inset 0 0 0 3px rgba(255, 255, 255, 0.3);
  background: ${phone_screen_background};
}
.phone-screen img {
  width: 100%;
  height: 100%;
  object-fit: contain;
  background: ${phone_screen_background};
}
.text-block {
  max-width: 420px;
  color: ${text_color};
}
.title {
  font-size: 55px;
  font-weight: 700;
  line-height: 1.2;
  white-space: pre-line;
  margin-bottom: 40px;
}
.btn {
  padding: 18px 45px;
  background: ${button_bg_color};
  border-radius: 40px;
  display: inline-block;
  font-size: 28px;
  font-weight: 700;
  color: ${button_text_color};
  text-decoration: none;
  border: 2px solid ${button_border_color};
}
.footer {
  color: ${footer_text_color};
  font-size: 28px;
  position: absolute;
  bottom: 80px;
  right: 80px;
}
${custom_css}
