components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800;900&family=Poppins:wght@400;500;600;700;800;900&family=Playfair+Display:wght@400;500;600;700;800;900&display=swap');
* { margin: 0; padding: 0; box-sizing: border-box; }
body, html {
  width: 1080px;
  height: 1080px;
  margin: 0;
  padding: 0;
  font-family: 'Inter', 'Poppins', sans-serif;
  overflow: hidden;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
.container {
  width: 1080px;
  height: 1080px;
  position: relative;
  overflow: hidden;
  background: #0A0A0A;
}
/* Background Image */
.background-image {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}
.bg-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
  filter: brightness(0.6) contrast(1.1) saturate(0.9);
}
.background-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.2);
  z-index: 2;
}
/* Main Card */
.main-card {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 780px;
  height: 720px;
  border-radius: 30px;
  overflow: hidden;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
  z-index: 10;
}
/* Orange Header Section */
.orange-section {
  width: 100%;
  height: 300px;
  background: #FF6B35;
  position: relative;
  padding: 45px 60px 40px 60px;
  overflow: hidden;
}
/* Geometric Pattern Background */
.orange-pattern {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    repeating-linear-gradient(
      45deg,
      transparent,
      transparent 16px,
      rgba(200, 70, 30, 0.5) 16px,
      rgba(200, 70, 30, 0.5) 18px
    ),
    repeating-linear-gradient(
      -45deg,
      transparent,
      transparent 16px,
      rgba(200, 70, 30, 0.5) 16px,
      rgba(200, 70, 30, 0.5) 18px
    );
  background-size: 32px 32px;
  opacity: 0.8;
  z-index: 1;
}
/* Brand Name - White Serif */
.brand-name {
  font-size: 32px;
  font-weight: 600;
  color: #FFFFFF;
  font-family: 'Playfair Display', serif;
  position: relative;
  z-index: 3;
  margin-bottom: 35px;
  letter-spacing: 0.5px;
}
/* Main Heading - Large Bold Black Sans-serif */
.main-heading {
  font-size: 88px;
  font-weight: 900;
  color: #000000;
  font-family: 'Inter', sans-serif;
  text-align: left;
  margin-bottom: 20px;
  line-height: 0.95;
  letter-spacing: -2px;
  position: relative;
  z-index: 3;
}
/* Tagline - Small White Sans-serif, Left Aligned */
.tagline {
  font-size: 22px;
  font-weight: 400;
  color: #FFFFFF;
  font-family: 'Inter', sans-serif;
  text-align: left;
  position: relative;
  z-index: 3;
  letter-spacing: 0.2px;
}
/* White Section */
.white-section {
  width: 100%;
  height: 420px;
  background: #FFFFFF;
  padding: 55px 60px 60px 60px;
  display: flex;
  align-items: flex-start;
  justify-content: center;
}
/* Contact Info */
.contact-info {
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 32px;
}
.contact-item {
  display: flex;
  align-items: center;
  gap: 20px;
}
/* Circular Icon Wrappers */
.contact-icon-wrapper {
  width: 50px;
  height: 50px;
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #FFFFFF;
  border-radius: 50%;
  border: 2px solid;
}
.phone-icon-wrapper {
  border-color: #FF6B35;
}
.phone-icon-wrapper svg {
  width: 24px;
  height: 24px;
  stroke: #FF6B35;
}
.email-icon-wrapper {
  border-color: #000000;
}
.email-icon-wrapper svg {
  width: 24px;
  height: 24px;
  stroke: #000000;
}
.website-icon-wrapper {
  border-color: #FF6B35;
}
.website-icon-wrapper svg {
  width: 24px;
  height: 24px;
  stroke: #FF6B35;
}
.address-icon-wrapper {
  border-color: #000000;
}
.address-icon-wrapper svg {
  width: 24px;
  height: 24px;
  stroke: #000000;
}
/* Contact Text */
.contact-text {
  font-size: 32px;
  font-weight: 500;
  color: #000000;
  font-family: 'Inter', sans-serif;
  line-height: 1.4;
  letter-spacing: -0.3px;
}

