components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  width: 1080px;
  height: 1080px;
  font-family: 'Lato', Arial, sans-serif;
  overflow: hidden;
  background: ${primary_color};
}
.poster {
  width: 1080px;
  height: 1080px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  text-align: center;
  position: relative;
  color: ${secondary_color};
}
.header {
  padding-top: 80px;
}
.header img {
  height: 60px;
  object-fit: contain;
}
.content {
  flex: 1;
  width: 100%;
  padding: 0 120px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 20px;
}
.preheading {
  font-size: 40px;
  font-weight: 300;
}
.title {
  font-family: 'Anton', sans-serif;
  font-size: 180px;
  letter-spacing: 6px;
  line-height: 1;
  text-shadow: 3px 3px 10px rgba(0,0,0,0.7);
}
.subtitle {
  font-family: 'Anton', sans-serif;
  font-size: 80px;
  text-transform: uppercase;
  letter-spacing: 4px;
}
.footer {
  width: 100%;
  padding-bottom: 80px;
}
.footer .handle {
  font-size: 24px;
  letter-spacing: 2px;
}
${custom_css}
