components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700;800;900&display=swap');
* { margin: 0; padding: 0; box-sizing: border-box; }
body, html {
  width: 1200px;
  height: 627px;
  margin: 0;
  padding: 0;
  font-family: 'Poppins', sans-serif;
  overflow: hidden;
}
.container {
  width: 1200px;
  height: 627px;
  position: relative;
  display: flex;
  overflow: hidden;
}
/* Left Section - Image */
.left-section {
  width: 480px;
  height: 627px;
  position: relative;
  overflow: hidden;
  flex-shrink: 0;
}
.background-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
}
/* Right Section - Content */
.right-section {
  width: 720px;
  height: 627px;
  background: #000000;
  position: relative;
  padding: 66px 60px;
  display: flex;
  flex-direction: column;
  flex-shrink: 0;
}
/* Logo */
.logo {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 48px;
}
.logo-icon {
  width: 54px;
  height: 54px;
  object-fit: contain;
  flex-shrink: 0;
}
.logo-text {
  display: flex;
  flex-direction: column;
  line-height: 1.2;
}
.logo-primary {
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
}
.logo-secondary {
  font-size: 20px;
  font-weight: 400;
  color: #C4C4C4;
  font-family: 'Poppins', sans-serif;
}
/* Headline */
.headline {
  font-size: 50px;
  font-weight: 800;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
  line-height: 1.3;
  margin-bottom: 20px;
  white-space: pre-wrap;
  max-width: 500px;
}
/* Subtitle */
.subtitle {
  font-size: 18px;
  font-weight: 400;
  color: #C4C4C4;
  font-family: 'Poppins', sans-serif;
  line-height: 1.4;
  margin-bottom: 25px;
  max-width: 480px;
}
/* Benefits */
.benefits {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-bottom: 30px;
}
.benefit-item {
  display: flex;
  align-items: center;
  gap: 10px;
}
.checkmark {
  font-size: 20px;
  font-weight: 700;
  color: #F6711D;
  font-family: 'Poppins', sans-serif;
  flex-shrink: 0;
}
.benefit-text {
  font-size: 16px;
  font-weight: 500;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
}
/* CTA Button */
.cta-button {
  width: 220px;
  height: 48px;
  background: #F6711D;
  border-radius: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 18px;
  font-weight: 600;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
  padding: 12px 25px;
  margin-top: auto;
  cursor: pointer;
}
/* Decorative Squares - Positioned from container */
.square {
  width: 40px;
  height: 40px;
  background: #F6711D;
  position: absolute;
}
.square-1 {
  top: 503px;
  left: 1157px;
}
.square-2 {
  top: 585px;
  left: 1075px;
}
.square-3 {
  top: 585px;
  left: 1157px;
}
.square-4 {
  top: 544px;
  left: 1116px;
}

${custom_css}
