components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  background: ${primary_color};
  font-family: Arial, sans-serif;
  overflow: hidden;
  text-align: center;
  color: ${text_color};
  position: relative;
  display: flex;
  flex-direction: column;
}

/* Header 1: Logo in center */
.header-1 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 60px 4% 2% 4%;
  box-sizing: border-box;
  flex-shrink: 0;
}
.header-1 .logo {
  height: clamp(25px, 3.5vh, 40px);
  width: auto;
  max-width: 150px;
}

.wrapper {
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 0 80px;
  box-sizing: border-box;
}

/* Footer 1: Handle/website in center */
.footer-1 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 2% 4% 60px 4%;
  box-sizing: border-box;
  flex-shrink: 0;
}
.footer-1 .handle-website {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
  font-size: clamp(14px, 2vw, 22px);
  font-weight: 500;
  color: ${text_color};
  letter-spacing: 0.5px;
  text-align: center;
}

/* MAIN HEADING */
.heading {
  font-size: 70px;
  font-weight: bold;
  margin-bottom: 80px;
  line-height: 1.2;
}

/* BOX STYLES */
.box {
  padding: 50px 40px;
  border-radius: 40px;
  margin-bottom: 60px;
  background: white;
  text-align: center;
}

.title {
  font-size: 48px;
  font-weight: 700;
  margin-bottom: 20px;
}

.desc {
  font-size: 38px;
  line-height: 1.4;
  opacity: 0.85;
}

/* Highlighted truth box */
.truth {
  background: ${secondary_color};
  color: #ffffff;
}

${custom_css}
