components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800;900&display=swap');
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
body, html {
  width: 1080px;
  height: 1350px;
  font-family: 'Inter', sans-serif;
  background: linear-gradient(135deg, #EEF1F6 0%, #F8F9FA 100%);
  overflow: hidden;
}
.container {
  width: 100%;
  height: 100%;
  position: relative;
  padding: 40px;
}
/* Title */
.main-title {
  position: absolute;
  top: 90px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 110px;
  font-weight: 900;
  color: #4A4AFF;
  text-align: center;
  letter-spacing: -1px;
  width: 1000px;
  line-height: 1;
  opacity: 0.98;
  white-space: pre-wrap;
}
/* Subtitle */
.subtitle {
  position: absolute;
  top: 335px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 30px;
  letter-spacing: 3px;
  color: #555;
  text-align: center;
  opacity: 0.9;
  width: 900px;
  text-transform: uppercase;
}
/* Glassmorphism Card */
.card {
  position: absolute;
  top: 450px;
  left: 50%;
  transform: translateX(-50%);
  width: 920px;
  height: 560px;
  background: rgba(255, 255, 255, 0.4);
  backdrop-filter: blur(18px) saturate(160%);
  -webkit-backdrop-filter: blur(18px) saturate(160%);
  border: 1px solid rgba(255, 255, 255, 0.45);
  border-radius: 30px;
  display: flex;
  overflow: hidden;
  box-shadow: 0 25px 45px rgba(0,0,0,0.12);
}
/* Columns */
.column {
  flex: 1;
  padding: 55px 45px;
  position: relative;
  display: flex;
  flex-direction: column;
  backdrop-filter: blur(10px);
}
.myth-column {
  border-right: 1px solid rgba(255,255,255,0.45);
}
/* Headings */
.column-heading {
  font-size: 40px;
  font-weight: 800;
  margin-bottom: 35px;
  position: relative;
  padding-bottom: 12px;
  width: fit-content;
  text-transform: uppercase;
  letter-spacing: 1px;
}
.underline {
  height: 5px;
  width: 100%;
  position: absolute;
  bottom: 0;
  left: 0;
  border-radius: 10px;
}
.myth-underline { background: #66BB6A; }
.fact-underline { background: #EF5350; }
/* Items */
.item-list {
  display: flex;
  flex-direction: column;
  gap: 30px;
  flex: 1;
}
.item {
  font-size: 28px;
  line-height: 1.55;
  color: #1B1B1B;
  font-weight: 500;
  opacity: 0.92;
}
/* Footer */
.footer {
  position: absolute;
  bottom: 40px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 30px;
  font-weight: 800;
  opacity: 0.8;
  letter-spacing: 1px;
  text-align: center;
}

${custom_css}
