components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700;800;900&display=swap');
body, html {
  margin: 0;
  padding: 0;
  font-family: 'Inter', sans-serif;
  background: #F8F9FA;
}
.container {
  width: 1080px;
  height: 1350px;
  position: relative;
  background: #FFFFFF;
  overflow: hidden;
}
/* Dark blue palette */
:root {
  --primary-darkblue: ${primary_color};
  --secondary-blue: ${primary_color};
}
/* Top-right stripe â€“ now dark navy */
.stripe-top {
  position: absolute;
  top: 0;
  right: 0;
  width: 105px;
  height: 243px;
  background: var(--primary-darkblue);
}
/* Bottom large rectangle */
.bottom-rect {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 700px;
  height: 460px;
  background: var(--primary-darkblue);
}
/* Black panel on bottom right */
.black-rect {
  position: absolute;
  bottom: 0;
  right: 0;
  width: 380px;
  height: 460px;
  background: #000000;
}
/* Text styles */
.header-text {
  position: absolute;
  top: 180px;
  left: 80px;
  font-size: 32px;
  font-weight: 600;
  color: #000000;
  letter-spacing: 1px;
  text-transform: uppercase;
}
.main-text {
  position: absolute;
  top: 230px;
  left: 80px;
  font-size: 130px;
  font-weight: 900;
  color: #000000;
  line-height: 1.1;
  max-width: 800px;
  white-space: pre-wrap;
}
.partner-text {
  position: absolute;
  top: 670px;
  left: 80px;
  font-size: 120px;
  font-weight: 900;
  color: var(--primary-darkblue);
  line-height: 1;
}
.details-text {
  position: absolute;
  top: 1100px;
  left: 80px;
  font-size: 44px;
  font-weight: 500;
  color: #FFFFFF;
}
.website-text {
  position: absolute;
  top: 1140px;
  left: 80px;
  font-size: 44px;
  font-weight: 500;
  color: #FFFFFF;
}
.company-name {
  position: absolute;
  bottom: 180px;
  right: 18%;
  transform: translateX(50%);
  font-size: 38px;
  font-weight: 800;
  color: #FFFFFF;
}

${custom_css}
