components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  font-family: Arial, sans-serif;
  overflow: hidden;
  background: ${background_color};
  color: white;
  text-align: center;
  position: relative;
  display: flex;
  flex-direction: column;
}

/* Header 1: Logo in center */
.header-1 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 60px 4% 2% 4%;
  box-sizing: border-box;
  flex-shrink: 0;
}
.header-1 .logo {
  height: clamp(50px, 7vh, 80px);
  width: auto;
  max-width: 300px;
}

/* Body container - vertically centered */
.content {
  width: 100%;
  padding: 0 80px;
  box-sizing: border-box;
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.title {
  font-family: "Anton", sans-serif;
  font-size: 130px;
  font-weight: 400;
  margin-bottom: 20px;
  letter-spacing: 6px;
  line-height: 1.2;
  text-shadow:
    3px 3px 10px rgba(0,0,0,0.7),
    0px 6px 24px rgba(0,0,0,0.35),
    5px 0px 20px rgba(0,0,0,0.13);
}

.subtitle {
  font-family: "Lato", sans-serif;
  font-size: 40px;
  opacity: 0.9;
  font-weight: 300;
  margin-bottom: 60px;
}

/* Image container (16:9 aspect ratio) */
.image-block {
  position: relative;
  width: 100%;
  max-width: 900px;
  aspect-ratio: 16 / 9;
  margin: 0 auto;
  border-radius: 20px;
  overflow: hidden;
  display: block;
}
.image-block img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 0;
  display: block;
}

/* Footer 1: Handle/website in center */
.footer-1 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 2% 4% 60px 4%;
  box-sizing: border-box;
  flex-shrink: 0;
}
.footer-1 .handle-website {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
  font-size: clamp(14px, 2vw, 22px);
  font-weight: 500;
  color: #ffffff;
  letter-spacing: 0.5px;
  text-align: center;
}

${custom_css}
