components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  font-family: Arial, sans-serif;
  background: #000000;
}
.poster {
  width: 1080px;
  height: 1350px;
  background: ${background_color};
  color: ${primary_color};
  position: relative;
  overflow: hidden;
}
.content {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 100%;
  transform: translate(-50%, -50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
}

.title {
  font-size: 120px;
  font-weight: 600;
  letter-spacing: 3px;
  margin-bottom: 25px;
  color: ${primary_color};
}

.subtitle {
  font-size: 40px;
  font-weight: 300;
  margin-bottom: 80px;
  font-style: italic;
  color: ${primary_color};
}

.image-container img {
  width: 80%;
  border-radius: 4px;
}

/* Footer 8: Logo + handle/website in the center */
.footer-8 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 40px 4% 20px;
  box-sizing: border-box;
  margin-top: 60px;
}
.footer-8 .logo-handle-section {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
}
.footer-8 .logo {
  height: clamp(30px, 5vh, 50px);
  width: auto;
  max-width: 200px;
}
.footer-8 .handle-website {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
  font-size: clamp(14px, 2vw, 22px);
  font-weight: 500;
  color: ${primary_color};
  letter-spacing: 0.5px;
  text-align: center;
}

${custom_css}
