components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  width: 1080px;
  min-height: 1350px;
  background: ${primary_color};
  color: ${secondary_color};
  font-family: Arial, sans-serif;
  overflow: visible;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 80px 60px 60px;
}
.canvas {
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 60px;
}
.title {
  font-size: 80px;
  font-weight: 800;
  text-align: center;
  letter-spacing: 2px;
  color: ${title_color};
}
.box {
  width: 100%;
  max-width: 900px;
  padding: 60px;
  border-radius: 30px;
  border: 4px solid ${box_border_color};
  box-shadow: 6px 6px 0px ${box_shadow_color};
  text-align: center;
}
.label {
  display: inline-block;
  background: ${label_background_color};
  color: ${label_text_color};
  padding: 12px 30px;
  border-radius: 12px;
  font-size: 32px;
  font-weight: 700;
  margin-bottom: 25px;
}
.question-box {
  background: ${question_box_color};
}
.answer-box {
  background: ${answer_box_color};
}
.box-text {
  font-size: 42px;
  line-height: 1.4;
  font-weight: 600;
  color: ${text_color};
}
.footer {
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}
.footer-8 {
  padding: 20px 0 0 0;
}
.footer-8 .logo-handle {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
}
.footer-8 .logo {
  width: 180px;
  height: auto;
}
.footer-8 .handle {
  font-size: 28px;
  font-weight: 600;
  letter-spacing: 2px;
  color: ${secondary_color};
}
${custom_css}
