components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700;800;900&display=swap');
* { margin: 0; padding: 0; box-sizing: border-box; }
body, html {
  width: 1080px;
  height: 1080px;
  margin: 0;
  padding: 0;
  font-family: 'Poppins', sans-serif;
  overflow: hidden;
}
.container {
  width: 1080px;
  height: 1080px;
  position: relative;
  background: #1A2047;
  overflow: hidden;
}
/* Background Decorative Elements */
.dots-pattern {
  position: absolute;
  width: 120px;
  height: 120px;
  background-image: radial-gradient(circle, rgba(255, 255, 255, 0.6) 2px, transparent 2px);
  background-size: 18px 18px;
  z-index: 1;
}
.dots-top-right {
  top: 50px;
  right: 50px;
}
.dots-bottom-left {
  bottom: 50px;
  left: 50px;
}
.wave-shapes {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 150px;
  background: linear-gradient(to top, rgba(147, 51, 234, 0.2), transparent);
  clip-path: ellipse(100% 50% at 50% 100%);
  z-index: 1;
}
/* Main White Card */
.white-card {
  position: absolute;
  top: 60px;
  left: 60px;
  width: 800px;
  height: 750px;
  background: #FFFFFF;
  border-radius: 40px;
  padding: 60px 50px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  position: relative;
  z-index: 10;
  overflow: visible;
}
/* Brand Name */
.brand-name {
  position: absolute;
  top: 60px;
  right: 50px;
  font-size: 22px;
  font-weight: 700;
  color: #000000;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 40px;
}
/* Main Heading */
.main-heading {
  font-size: 90px;
  font-weight: 900;
  color: #333333;
  font-family: 'Poppins', sans-serif;
  line-height: 1.1;
  margin-bottom: 25px;
  margin-top: 30px;
  white-space: pre-wrap;
}
/* Tagline */
.tagline {
  font-size: 22px;
  font-weight: 400;
  color: #333333;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 45px;
  line-height: 1.4;
}
/* Benefits List */
.benefits-list {
  display: flex;
  flex-direction: column;
  gap: 25px;
  margin-bottom: 80px;
}
.benefit-item {
  display: flex;
  align-items: center;
  gap: 15px;
}
.checkmark-icon {
  width: 28px;
  height: 28px;
  border-radius: 50%;
  background: #1A2047;
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 700;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}
.benefit-item span {
  font-size: 22px;
  font-weight: 500;
  color: #333333;
  font-family: 'Poppins', sans-serif;
}
/* Circular Photo */
.photo-circle {
  position: absolute;
  bottom: -80px;
  right: -80px;
  width: 300px;
  height: 300px;
  border-radius: 50%;
  overflow: hidden;
  border: 8px solid #FFFFFF;
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.2);
  z-index: 20;
}
.photo-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
}
/* Purple CTA Box */
.cta-box {
  position: absolute;
  bottom: 80px;
  left: 80px;
  background: #9333EA;
  border-radius: 25px;
  padding: 30px 35px;
  min-width: 300px;
  z-index: 25;
  box-shadow: 0 15px 40px rgba(147, 51, 234, 0.4);
}
.cta-top-row {
  display: flex;
  align-items: flex-start;
  gap: 15px;
  margin-bottom: 12px;
}
.cta-main-text {
  font-size: 36px;
  font-weight: 800;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
  line-height: 1.2;
  white-space: pre-wrap;
  flex: 1;
}
.globe-icon {
  font-size: 28px;
  flex-shrink: 0;
  margin-top: 4px;
}
.cta-website {
  font-size: 18px;
  font-weight: 500;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
  padding-left: 0;
}

${custom_css}
