components.css_content:
{ box-sizing: border-box; }
html, body { margin: 0; padding: 0; }
body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1080px;
  font-family: 'Poppins', sans-serif;
  background: rgb(245, 242, 239);
  position: relative;
}

.container {
  position: relative;
  width: 1080px;
  height: 1080px;
  overflow: hidden;
}

.headline {
  position: absolute;
  top: 140px;
  width: 100%;
  text-align: center;
  font-size: 58px;
  font-weight: 600;
  color: ${headline_color};
}

.card {
  position: absolute;
  top: 300px;
  left: 90px;
  width: 900px;
  background: ${card_bg};
  border-radius: 40px;
  display: flex;
  padding: 60px 80px;
  box-sizing: border-box;
}

.icon-box {
  width: 110px;
  height: 110px;
  background: ${icon_box_bg};
  border-radius: 30px 0 0 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  left: -80px;
}

.icon-circle {
  width: 60px;
  height: 60px;
  background: #ffffff;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.profile-icon {
  width: 34px;
  height: 34px;
  opacity: 0.85;
}

.text-block {
  margin-left: -50px;
  width: 600px;
}

.name {
  font-size: 42px;
  font-weight: 600;
  color: ${name_color};
  margin-bottom: 25px;
}

.quote {
  font-size: 32px;
  line-height: 1.35;
  color: ${quote_color};
  margin-bottom: 40px;
}

.line {
  width: 100%;
  height: 2px;
  background: ${icon_box_bg};
  margin-bottom: 20px;
}

.role {
  font-size: 26px;
  font-weight: 300;
  font-style: italic;
  color: ${role_color};
}

.company-name {
  position: absolute;
  bottom: 70px;
  right: 70px;
  font-size: 34px;
  font-weight: 500;
  color: ${company_color};
}

/* allow template-level overrides */
${custom_css}
