components.css_content:
body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1080px;
  overflow: hidden;
  background: ${accent_primary_color};
  font-family: Arial, sans-serif;
  color: ${text_color_primary};
  position: relative;
}

.header {
  position: absolute;
  top: 60px;
  left: 60px;
}

.header img {
  height: 30px;
  width: auto;
}

.content {
  position: absolute;
  top: 200px;
  left: 60px;
  width: 70%;
}

.heading {
  font-size: 90px;
  font-weight: bold;
  line-height: 1.15;
  margin-bottom: 20px;
  text-transform: none;
}

.heading .accent {
  color: ${text_color_secondary};
}

.cta {
  margin-top: 40px;
  display: inline-block;
  padding: 14px 28px;
  border: 2px solid ${accent_secondary_color};
  font-size: 32px;
  font-weight: 500;
  color: ${accent_secondary_color};
  text-decoration: none;
}

.footer {
  position: absolute;
  bottom: 60px;
  left: 60px;
  font-size: 22px;
  opacity: 0.9;
}

.illustration {
  position: absolute;
  bottom: 80px;
  right: 80px;
  width: 600px;
  opacity: 0.7;
}

${custom_css}
