components.css_content:
/* Container styles */
* { box-sizing: border-box; margin: 0; padding: 0; }
html, body { margin: 0; padding: 0; overflow: hidden; }
body {
  font-family: 'Outfit', sans-serif;
  background: ${primary_color};
  width: 1080px;
  height: 1080px;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  color: rgb(255, 255, 255);
  position: relative;
  overflow: hidden;
}

.container {
  width: 1080px;
  height: 1080px;
  position: relative;
  overflow: hidden;
  background: ${primary_color};
}

/* Decorative Circles - Top Right */
.decorative-circle {
  position: absolute;
  border-radius: 50%;
  z-index: 1;
}

/* Circle at (1000, 210), radius 180, fill secondary color, outline secondary color width 60 */
.circle-top-right-outer {
  width: 360px;
  height: 360px;
  background: ${secondary_color};
  border: 60px solid ${secondary_color};
  top: 30px;
  left: 820px;
}

/* Circle at (1000, 210), radius 160, outline primary color width 4 */
.circle-top-right-middle {
  width: 320px;
  height: 320px;
  border: 4px solid ${primary_color};
  top: 50px;
  left: 840px;
  background: transparent;
}

/* Circle at (860, 50), radius 130, fill lighter secondary, outline primary color width 4 */
.circle-top-right-inner {
  width: 260px;
  height: 260px;
  background: color-mix(in srgb, ${secondary_color} 85%, white);
  border: 4px solid ${primary_color};
  top: -80px;
  left: 730px;
}

/* Bottom left circle at (50, 1060), radius 220, fill lighter secondary, outline primary color width 4 */
.circle-bottom-left-inner {
  width: 440px;
  height: 440px;
  background: color-mix(in srgb, ${secondary_color} 92%, white);
  border: 4px solid ${primary_color};
  top: 840px;
  left: -170px;
}

/* Circle at (50, 1060), radius 200, outline black width 4 */
.circle-bottom-left-outer {
  width: 400px;
  height: 400px;
  border: 4px solid rgb(0, 0, 0);
  top: 860px;
  left: -150px;
  background: transparent;
}

/* Main Heading */
.headline-primary {
  position: absolute;
  top: 240px;
  left: 80px;
  font-size: 155px;
  font-weight: 900;
  color: rgb(255, 255, 255);
  font-family: 'Barlow', sans-serif;
  line-height: 1;
  letter-spacing: 10px;
  text-transform: uppercase;
  z-index: 10;
  max-width: 850px;
}

/* Subheading */
.subheading-secondary {
  position: absolute;
  top: 590px;
  left: 80px;
  font-size: 35px;
  font-weight: 400;
  color: rgb(255, 255, 255);
  font-family: 'Outfit', sans-serif;
  z-index: 10;
  max-width: 850px;
  line-height: 1.4;
}

/* Job Roles */
.role-list {
  position: absolute;
  top: 690px;
  left: 80px;
  font-size: 30px;
  font-weight: 600;
  color: color-mix(in srgb, ${secondary_color} 95%, black);
  font-family: 'Outfit', sans-serif;
  z-index: 10;
  max-width: 750px;
  line-height: 1.6;
  text-transform: uppercase;
}

/* Contact Section */
.contact-section {
  position: absolute;
  bottom: 80px;
  left: 280px;
  z-index: 10;
  text-align: left;
}

.contact-label {
  font-size: 38px;
  font-weight: 400;
  color: rgb(255, 255, 255);
  font-family: 'Outfit', sans-serif;
  margin-bottom: 20px;
  line-height: 1.2;
}

.contact-email {
  font-size: 38px;
  font-weight: 700;
  color: rgb(255, 255, 255);
  font-family: 'Outfit', sans-serif;
  line-height: 1.2;
}

/* Allow template-level CSS overrides */
${custom_css}
