components.css_content:
body {
  margin: 0;
  font-family: 'Segoe UI', Arial, sans-serif;
  background-color: ${background_color};
  color: ${primary_text_color};
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  padding: 40px 20px;
}
.container {
  width: 100%;
  max-width: 900px;
  background: ${container_bg_color};
  padding: 60px 40px;
  box-shadow: 0 4px 20px rgba(0,0,0,0.05);
}
.header {
  text-align: center;
  margin-bottom: 100px;
  position: relative;
}
.site-name {
  position: absolute;
  top: -30px;
  right: 0;
  font-size: 14px;
  color: ${primary_text_color};
  font-weight: 600;
}
h1 {
  font-size: 82px;
  font-weight: 900;
  margin: 0 0 30px 0;
  line-height: 1;
  letter-spacing: -2px;
}
h1 span {
  color: ${accent_color};
}
.badge {
  display: inline-block;
  background: ${container_bg_color};
  color: ${primary_text_color};
  font-size: 18px;
  font-weight: 600;
  padding: 10px 34px;
  border-radius: 50px;
  border: 3px solid ${accent_color};
  box-shadow: 0 4px 15px rgba(255,87,34,0.15);
}
.reasons {
  display: flex;
  flex-direction: column;
  gap: 38px;
  position: relative;
}
.reasons::before {
  content: '';
  position: absolute;
  left: 5%;
  transform: translateX(-50%);
  top: 80px;
  bottom: 40px;
  width: 2px;
  background: ${line_color};
  z-index: 0;
}
.reason {
  display: flex;
  align-items: flex-start;
  gap: 25px;
  position: relative;
  z-index: 1;
}
.number {
  flex-shrink: 0;
  width: 70px;
  height: 70px;
  background: ${number_bg_color};
  color: ${number_text_color};
  font-size: 36px;
  font-weight: bold;
  border: 4px solid ${accent_color};
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 4px 15px rgba(255,87,34,0.15);
  z-index: 2;
}
.content h3 {
  margin: 0 0 8px 0;
  font-size: 24px;
  font-weight: 700;
  color: ${primary_text_color};
}
.content p {
  margin: 0;
  font-size: 17px;
  line-height: 1.6;
  color: ${secondary_text_color};
}
${custom_css}
