components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@600&family=Inter:wght@400;500;600;700&display=swap');
* { margin: 0; padding: 0; box-sizing: border-box; }
html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1080px;
  font-family: 'Inter', sans-serif;
  background: ${background_color};
  color: #1f1f1f;
  overflow: hidden;
}
.canvas {
  width: 1080px;
  height: 1080px;
  background: ${canvas_background_color};
  display: flex;
  flex-direction: column;
  border-radius: 0;
  overflow: hidden;
  box-shadow: 0 25px 70px rgba(31, 31, 31, 0.18);
  position: relative;
}
.canvas::after {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  width: 130px;
  height: 100%;
  background: ${accent_color};
  z-index: 1;
}
.header {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 80px;
  padding: 25px 50px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  z-index: 10;
}
.header .logo {
  height: 30px;
  width: auto;
}
.website-link {
  font-family: 'Inter', sans-serif;
  font-size: 20px;
  font-weight: 600;
  color: #1f1f1f;
  text-decoration: none;
  letter-spacing: 0.5px;
}
.website-link:hover {
  text-decoration: underline;
}
.content-wrapper {
  flex: 1;
  display: flex;
  position: relative;
  z-index: 2;
}
.text-panel {
  flex: 0 0 50%;
  padding: 0 70px 0 110px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 40px;
  position: relative;
}
.hero-title {
  font-family: 'Playfair Display', serif;
  font-weight: 600;
  font-size: 40px;
  line-height: 1.15;
  color: ${hero_text_color};
}
.hero-title strong {
  font-weight: 600;
}
.tips {
  display: flex;
  flex-direction: column;
  gap: 32px;
}
.tip-card {
  position: relative;
  padding: 24px 34px 24px 50px;
  border-radius: 0;
  background: ${tip_card1_background};
  border: 1px solid ${tip_border_color};
  box-shadow: 0 12px 35px rgba(26, 12, 8, 0.06);
}
.tip-card:nth-child(2) {
  background: ${tip_card2_background};
}
.tip-card:nth-child(3) {
  background: ${tip_card3_background};
}
.icon-badge {
  position: absolute;
  left: -36px;
  top: 50%;
  transform: translateY(-50%);
  width: 72px;
  height: 72px;
  border-radius: 50%;
  background: ${accent_color};
  display: flex;
  align-items: center;
  justify-content: center;
  color: #ffffff;
  font-size: 36px;
}
.tip-content h3 {
  font-size: 30px;
  font-weight: 600;
  letter-spacing: 1px;
  margin-bottom: 10px;
}
.tip-content p {
  font-size: 18px;
  line-height: 1.5;
  color: ${tip_body_color};
  max-width: 420px;
}
.footer-handle {
  position: absolute;
  bottom: 40px;
  left: 110px;
  font-size: 20px;
  letter-spacing: 4px;
  font-weight: 600;
  color: ${footer_text_color};
  z-index: 3;
}
.media-panel {
  flex: 0 0 50%;
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 80px 60px 80px 30px;
  overflow: hidden;
  gap: 28px;
}
.pink-strip {
  position: absolute;
  right: 0;
  top: 0;
  width: 220px;
  height: 100%;
  background: ${accent_color};
}
.photo-frame {
  position: relative;
  width: 88%;
  height: 88%;
  border-radius: 0;
  overflow: hidden;
  box-shadow: 0 35px 60px rgba(53, 34, 39, 0.35);
  z-index: 2;
  background: #000000;
}
.photo-frame img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}
${custom_css}
