components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  font-family: 'Inter', Arial, sans-serif;
  background: #000000;
}
.poster {
  width: 1080px;
  height: 1350px;
  background-image: url('${bg_image_url}');
  background-size: cover;
  background-position: center;
  color: ${text_color};
  position: relative;
  overflow: hidden;
}
.content {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 90%;
  transform: translate(-50%, -50%);
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.phone-wrapper {
  position: relative;
  width: 520px;
}
.phone {
  width: 100%;
  position: relative;
  z-index: 2;
}
.screen {
  position: absolute;
  top: 40px;
  left: 58px;
  width: 404px;
  height: 840px;
  border-radius: 35px;
  object-fit: contain;
  background: ${screen_background_color};
  z-index: 1;
}
.text-block {
  max-width: 450px;
  text-align: left;
  margin-right: 30px;
}
.logo {
  width: 130px;
  margin-bottom: 40px;
}
.title {
  font-size: 60px;
  font-weight: 700;
  line-height: 1.2;
  white-space: pre-line;
  margin-bottom: 30px;
}
.subtitle {
  font-size: 50px;
  line-height: 1.2;
  color: ${subtitle_color};
  white-space: pre-line;
  text-shadow: 0 0 12px rgba(255, 255, 255, 0.7);
  margin-bottom: 40px;
}
.small-text {
  font-size: 28px;
  line-height: 1.4;
  color: ${small_text_color};
  opacity: 0.9;
  white-space: pre-line;
}
${custom_css}
