components.css_content:
* { margin:0; padding:0; box-sizing:border-box; }

body {
  background: ${background_color};
  font-family: 'Inter', sans-serif;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  width: 1080px;
  height: 1080px;
  margin: 0;
  padding: 0;
}

.post {
  width: 1080px;
  height: 1080px;
  background: white;
  position: relative;
  overflow: hidden;
  box-shadow: 0 30px 80px rgba(0,0,0,0.15);
}

/* Background subtle circles */
.post::before {
  content:'';
  position:absolute;
  width:900px;
  height:900px;
  background: rgba(44,74,95,0.05);
  border-radius:50%;
  top:-250px;
  left:-350px;
}

.post::after {
  content:'';
  position:absolute;
  width:800px;
  height:800px;
  background: rgba(44,74,95,0.04);
  border-radius:50%;
  bottom:-150px;
  right:-250px;
}

/* Dark blue header shape */
.header {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 400px;
  background: ${primary_color};
  border-bottom-left-radius: 50% 28%;
  border-bottom-right-radius: 50% 28%;
}

/* Company name */
.company {
  position: absolute;
  top: 60px;
  right: 70px;
  font-size: 28px;
  font-weight: 900;
  color: #FFFFFF;
  text-align: right;
}

.company span {
  font-size: 18px;
  font-weight: 500;
  color: rgba(255, 255, 255, 0.9);
  display: block;
  margin-top: 4px;
}

/* Main title */
.title {
  position: absolute;
  top: 80px;
  left: 70px;
  color: white;
  font-size: 100px;
  font-weight: 900;
  line-height: 0.92;
  letter-spacing: 0px;
}

/* Circle image - EXACT size and position as your original */
.circle-img {
  position: absolute;
  top: 170px;
  right: 0px;
  width: 300px;
  height: 300px;
  border-radius: 50%;
  overflow: hidden;
  border: 10px solid white;
  box-shadow: 0 25px 50px rgba(0,0,0,0.25);
}

.circle-img img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* Services */
.services {
  position: absolute;
  top: 500px;
  left: 70px;
  right: 70px;
}

.services h2 {
  font-size: 48px;
  font-weight: 900;
  color: #111;
  margin-bottom: 40px;
}

.grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 30px 100px;
  font-size: 34px;
  font-weight: 500;
  color: #222;
}

/* Contact bar */
.contact {
  position: absolute;
  bottom: 80px;
  left: 70px;
  right: 70px;
  background: ${primary_color};
  padding: 35px 60px;
  border-radius: 80px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  color: white;
}

.phone {
  display: flex;
  align-items: center;
  gap: 25px;
}

.icon {
  width: 75px;
  height: 75px;
  background: ${secondary_color};
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.icon svg {
  width: 40px;
  height: 40px;
  stroke: ${primary_color};
  stroke-width: 3;
}

.number {
  font-size: 44px;
  font-weight: 700;
}

.btn {
  background: ${secondary_color};
  color: ${primary_color};
  font-size: 44px;
  font-weight: 900;
  padding: 22px 65px;
  border-radius: 70px;
  text-decoration: none;
}

${custom_css}
