components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  font-family: Arial, sans-serif;
  overflow: hidden;
  color: white;
  position: relative;
  background-image: url('${background_image_url}');
  background-size: cover;
  background-position: center;
}

/* Dark overlay */
body::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  z-index: 1;
}

/* Header 6: Logo in center, handle/website below */
.header-6 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 40px 4%;
  box-sizing: border-box;
  position: relative;
  z-index: 2;
}
.header-6 .logo-section {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  margin-top: 20px;
}
.header-6 .logo {
  height: clamp(30px, 5vh, 50px);
  width: auto;
  max-width: 200px;
}
.header-6 .handle-website {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
  font-size: clamp(12px, 1.8vw, 20px);
  font-weight: 500;
  color: #ffffff;
  text-align: center;
  letter-spacing: 0.5px;
}

/* Text wrapper */
.content {
  position: absolute;
  top: 180px;
  width: 100%;
  text-align: center;
  padding: 0 60px;
  box-sizing: border-box;
  z-index: 2;
  height: calc(100% - 180px);
  overflow: hidden;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
}

/* Title */
.title {
  font-size: 130px;
  font-weight: 600;
  line-height: 1;
  margin-bottom: 20px;
  flex-shrink: 0;
}

/* Subtitle under title */
.title span {
  display: block;
  font-size: 120px;
  font-weight: 300;
  font-style: italic;
  margin-top: 10px;
}

.subtitle {
  margin-top: 40px;
  font-size: 44px;
  opacity: 0.9;
  margin-bottom: 60px;
  flex-shrink: 0;
}

/* CTA */
.cta {
  background: white;
  color: #333;
  display: inline-block;
  padding: 25px 60px;
  font-size: 40px;
  margin-bottom: 80px;
  flex-shrink: 0;
  width: auto;
  align-self: center;
}

/* Bottom image (3:4) */
.bottom-image {
  width: 50%;
  aspect-ratio: 3 / 4;
  object-fit: cover;
  display: block;
  margin: 0 auto;
  margin-top: -20px;
  flex-shrink: 0;
  max-height: calc(100% - 20px);
}

${custom_css}
