components.css_content:
body {
  margin: 0;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: ${background_color};
  color: ${primary_text_color};
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-image: radial-gradient(circle at 10% 20%, rgba(220, 245, 230, 0.3) 0%, transparent 20%), radial-gradient(circle at 90% 80%, rgba(200, 240, 210, 0.3) 0%, transparent 20%);
}
.container {
  position: relative;
  width: 90%;
  max-width: 1000px;
  padding: 60px 20px;
  text-align: center;
}
h1 {
  font-size: 96px;
  font-weight: 800;
  color: ${title_color};
  margin: 0 0 80px;
  letter-spacing: -3px;
}
.faq-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(340px, 1fr));
  gap: 50px 70px;
  max-width: 960px;
  margin: 0 auto;
}
.faq-item {
  background: ${card_bg_color};
  color: ${card_text_color};
  padding: 34px 30px;
  border-radius: 20px;
  box-shadow: 10px 10px 30px rgba(10, 107, 66, 0.25);
  text-align: left;
  position: relative;
  transform: rotate(-2deg);
}
.faq-item:nth-child(2) { transform: rotate(3deg); }
.faq-item:nth-child(3) { transform: rotate(-1deg); }
.faq-item:nth-child(4) { transform: rotate(2deg); }
.faq-item:nth-child(5) { transform: rotate(-3deg); }
.faq-item:nth-child(6) { transform: rotate(1deg); }
.q {
  font-size: 24px;
  font-weight: 700;
  margin: 0 0 16px 0;
}
.q::before {
  content: "Q";
  font-size: 38px;
  font-weight: 900;
  margin-right: 14px;
  opacity: 0.95;
}
.a {
  font-size: 22px;
  line-height: 1.55;
  margin: 0;
}
.a::before {
  content: "A";
  font-size: 38px;
  font-weight: 900;
  margin-right: 14px;
  float: left;
  line-height: 1;
}
.footer {
  margin-top: 80px;
  font-size: 22px;
  color: ${footer_text_color};
}
.brand {
  font-size: 32px;
  font-weight: 700;
  color: ${brand_color};
}
.bg-shape {
  position: absolute;
  border: 2px solid ${shape_border_color};
  border-radius: 20px;
  transform: rotate(45deg);
  pointer-events: none;
}
.shape1 { width: 320px; height: 320px; top: 8%; left: -8%; }
.shape2 { width: 420px; height: 420px; bottom: 5%; right: -12%; transform: rotate(30deg); }
@media (max-width: 768px) {
  h1 { font-size: 68px; margin-bottom: 60px; }
  .faq-grid { gap: 40px; }
  .faq-item { padding: 28px 24px; transform: none !important; }
  .q, .a { font-size: 20px; }
  .q::before, .a::before { font-size: 34px; }
}
${custom_css}
