components.css_content:
/* Container styles */
* { box-sizing: border-box; margin: 0; padding: 0; }
html, body { margin: 0; padding: 0; overflow: hidden; }
body {
  font-family: 'Poppins', sans-serif;
  background: ${primary_color};
  width: 1080px;
  height: 1080px;
  display: flex;
  justify-content: center;
  align-items: center;
  color: rgb(255, 255, 255);
  position: relative;
  overflow: hidden;
}

.container {
  width: 1080px;
  height: 1080px;
  position: relative;
  overflow: hidden;
  background: ${primary_color};
}

/* Background Square Pattern */
.square-pattern {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    linear-gradient(rgba(200, 200, 200, 0.6) 1px, transparent 1px),
    linear-gradient(90deg, rgba(200, 200, 200, 0.6) 1px, transparent 1px);
  background-size: 60px 60px;
  background-position: 0 0, 0 0;
  z-index: 2;
  pointer-events: none;
}

/* Background Grid Pattern */
.grid-pattern {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    repeating-linear-gradient(0deg, #d85028 0px, transparent 1px, transparent 40px, #d85028 41px),
    repeating-linear-gradient(90deg, #d85028 0px, transparent 1px, transparent 40px, #d85028 41px);
  background-size: 40px 40px;
  opacity: 0.3;
  z-index: 1;
}

/* Brand Name */
.brand-name {
  position: absolute;
  top: 60px;
  right: 60px;
  font-size: 36px;
  font-weight: 600;
  color: rgb(255, 255, 255);
  font-family: 'Poppins', sans-serif;
  z-index: 20;
  letter-spacing: 2px;
  text-align: right;
}

/* Flash Sale Section */
.flash-sale-section {
  position: absolute;
  top: 100px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 20;
  width: 100%;
}

.flash-sale-text {
  text-align: center;
}

.flash-line {
  font-size: 170px;
  font-weight: 900;
  color: rgb(255, 255, 255);
  font-family: 'Poppins', sans-serif;
  line-height: 0.9;
  letter-spacing: 8px;
  text-transform: uppercase;
  margin-bottom: 0;
}

.sale-line {
  font-size: 170px;
  font-weight: 900;
  color: rgb(255, 255, 255);
  font-family: 'Poppins', sans-serif;
  line-height: 0.9;
  letter-spacing: 8px;
  text-transform: uppercase;
  margin-top: -10px;
}

/* Discount Offer */
.discount-offer {
  position: absolute;
  top: 400px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 70px;
  font-weight: 800;
  color: ${secondary_color};
  font-family: 'Poppins', sans-serif;
  text-align: center;
  z-index: 20;
  letter-spacing: 3px;
  text-shadow: 0 3px 10px color-mix(in srgb, ${secondary_color} 40%, transparent);
}

/* Countdown Timer Box */
.timer-box {
  position: absolute;
  top: 520px;
  left: 50%;
  transform: translateX(-50%);
  width: 900px;
  background: rgb(255, 255, 255);
  border: 4px solid rgb(0, 0, 0);
  border-radius: 24px;
  padding: 50px 60px;
  z-index: 20;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.25);
}

.timer-label {
  font-size: 50px;
  font-weight: 800;
  color: ${accent_color};
  font-family: 'Poppins', sans-serif;
  text-align: center;
  margin-bottom: 30px;
  letter-spacing: 3px;
  text-transform: uppercase;
  line-height: 1.2;
}

.timer-display {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 25px;
}

.timer-unit {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
}

.timer-number {
  width: 160px;
  height: 160px;
  background: rgb(255, 255, 255);
  border: 4px solid rgb(0, 0, 0);
  border-radius: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 80px;
  font-weight: 900;
  color: ${primary_color};
  font-family: 'Poppins', sans-serif;
  letter-spacing: 0;
  line-height: 1;
}

.timer-separator {
  font-size: 80px;
  font-weight: 900;
  color: ${accent_color};
  font-family: 'Poppins', sans-serif;
  display: flex;
  align-items: center;
  line-height: 1;
  height: 160px;
  margin-left: 10px;
  margin-right: 10px;
  margin-top: -45px;
}

.timer-unit-label {
  font-size: 22px;
  font-weight: 700;
  color: ${accent_color};
  font-family: 'Poppins', sans-serif;
  text-transform: uppercase;
  letter-spacing: 1px;
  margin-top: 4px;
}

/* Call to Action */
.cta-text {
  position: absolute;
  bottom: 40px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 36px;
  font-weight: 400;
  color: rgb(255, 255, 255);
  font-family: 'Poppins', sans-serif;
  text-align: center;
  z-index: 20;
  letter-spacing: 2px;
  white-space: nowrap;
  line-height: 1;
}

/* Allow template-level CSS overrides */
${custom_css}
