components.css_content:
body {
  margin: 0;
  padding: 0;
  background: #fff;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.post {
  width: 1080px;
  height: 1080px;
  background-image: url('${background_image_url}');
  background-size: cover;
  background-position: center;
  padding: 0;
  box-sizing: border-box;
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  font-family: "Georgia", serif;
  color: ${accent_secondary_color};
  position: relative;
}

.logo {
  width: 180px;
  height: auto;
  margin-bottom: 40px;
  align-self: center;
}

.heading-small {
  font-size: 32px;
  letter-spacing: 2px;
  margin-bottom: 20px;
  color: ${accent_secondary_color};
}

.heading-large {
  font-size: 120px;
  font-weight: 700;
  color: ${accent_primary_color};
  line-height: 1;
  margin-bottom: 25px;
}

.subtext {
  font-size: 38px;
  color: ${accent_secondary_color};
  letter-spacing: 1px;
  margin-bottom: 80px;
}

.website {
  font-size: 28px;
  color: #000;
  margin-top: 20px;
}

${custom_css}
