components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700;900&display=swap');
* { margin: 0; padding: 0; box-sizing: border-box; }
body, html {
  width: 1080px;
  height: 1080px;
  margin: 0;
  padding: 0;
  font-family: 'Poppins', sans-serif;
  overflow: hidden;
}
.container {
  width: 1080px;
  height: 1080px;
  position: relative;
  background: #F5F5F5;
  overflow: hidden;
}
/* Arrow Icon - Top Right */
.arrow-icon {
  position: absolute;
  top: 60px;
  right: 60px;
  width: 50px;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 42px;
  color: #000000;
  font-weight: 900;
  transform: rotate(45deg);
  opacity: 0.9;
  z-index: 10;
  line-height: 1;
  letter-spacing: 0;
}
/* Dots Pattern - Right Side - 4 Columns */
.dots-pattern {
  position: absolute;
  top: 280px;
  right: 60px;
  width: 20px;
  height: 600px;
  background-image: 
    radial-gradient(circle at 0% center, #000000 1.5px, transparent 1.5px),
    radial-gradient(circle at 33.33% center, #000000 1.5px, transparent 1.5px),
    radial-gradient(circle at 66.66% center, #000000 1.5px, transparent 1.5px),
    radial-gradient(circle at 100% center, #000000 1.5px, transparent 1.5px);
  background-size: 20px 24px;
  background-repeat: repeat-y;
  background-position: center top;
  opacity: 0.3;
  z-index: 1;
}
/* Logo */
.logo {
  position: absolute;
  bottom: 50px;
  left: 50px;
  width: 60px;
  height: 60px;
  background: #000000;
  border-radius: 4px;
  color: #FFFFFF;
  font-weight: 900;
  font-size: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: 'Poppins', sans-serif;
  z-index: 10;
}
/* Main Heading */
.hiring-title {
  position: absolute;
  top: 100px;
  left: 100px;
  font-size: 220px;
  font-weight: 900;
  color: #FF0000;
  line-height: 0.8;
  font-family: 'Poppins', sans-serif;
  z-index: 10;
  white-space: pre-wrap;
}
/* Job Role */
.role-title {
  position: absolute;
  top: 540px;
  left: 100px;
  font-size: 64px;
  font-weight: 700;
  color: #000000;
  font-family: 'Poppins', sans-serif;
  z-index: 10;
}
/* Bullet Points */
.bullet-list {
  position: absolute;
  top: 670px;
  left: 100px;
  z-index: 10;
}
.bullet-item {
  font-size: 26px;
  font-weight: 400;
  color: #000000;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 20px;
  line-height: 1.4;
}
.bullet-item::before {
  content: '• ';
  font-weight: 700;
}
/* Dividers */
.divider-1 {
  position: absolute;
  top: 850px;
  left: 100px;
  right: 100px;
  height: 1px;
  background: #000000;
  z-index: 10;
}
.divider-2 {
  position: absolute;
  bottom: 150px;
  left: 100px;
  right: 100px;
  height: 1px;
  background: #000000;
  z-index: 10;
}
/* CTA Section */
.cta-section {
  position: absolute;
  top: 850px;
  bottom: 150px;
  left: 100px;
  right: 100px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 10;
}
.cta-with-icon {
  display: flex;
  align-items: center;
  gap: 15px;
}
.checkmark-icon {
  width: 40px;
  height: 40px;
  background: #000000;
  border-radius: 50%;
  color: #FFFFFF;
  font-size: 24px;
  font-weight: 700;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}
.cta-text {
  font-size: 24px;
  font-weight: 700;
  color: #000000;
  font-family: 'Poppins', sans-serif;
}
.apply-section {
  text-align: right;
}
.apply-instruction {
  font-size: 18px;
  font-weight: 400;
  color: #000000;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 8px;
}
.apply-email {
  font-size: 22px;
  font-weight: 600;
  color: #000000;
  font-family: 'Poppins', sans-serif;
}
/* Company Name */
.company-name {
  position: absolute;
  bottom: 60px;
  right: 50px;
  font-size: 24px;
  font-weight: 500;
  color: #000000;
  font-family: 'Poppins', sans-serif;
  z-index: 10;
}

${custom_css}
