components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700;900&display=swap');
* { margin: 0; padding: 0; box-sizing: border-box; }
body, html {
  width: 1080px;
  height: 1080px;
  margin: 0;
  padding: 0;
  font-family: 'Poppins', sans-serif;
  overflow: hidden;
}
.container {
  width: 1080px;
  height: 1080px;
  position: relative;
  background: ${background_color};
  overflow: hidden;
}
/* Main Heading */
.main-heading {
  position: absolute;
  top: 200px;
  left: 80px;
  font-size: 120px;
  font-weight: 900;
  color: #FFFFFF;
  line-height: 1.1;
  font-family: 'Poppins', sans-serif;
  z-index: 10;
  white-space: pre-wrap;
}
/* Introductory Text */
.intro-text {
  position: absolute;
  top: 500px;
  left: 80px;
  font-size: 32px;
  font-weight: 400;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
  z-index: 10;
  line-height: 1.4;
  max-width: 900px;
}
/* Job Title Section */
.job-title-section {
  position: absolute;
  top: 620px;
  left: 80px;
  display: flex;
  align-items: center;
  gap: 15px;
  z-index: 10;
  flex-wrap: wrap;
}
.hiring-prefix {
  font-size: 36px;
  font-weight: 500;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
  white-space: nowrap;
}
.job-title-box {
  display: inline-block;
  padding: 12px 24px;
  background: ${accent_color};
  border-radius: 8px;
  font-size: 36px;
  font-weight: 600;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
  white-space: nowrap;
}
/* Company Name */
.company-name {
  position: absolute;
  bottom: 60px;
  left: 80px;
  font-size: 32px;
  font-weight: 500;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
  z-index: 10;
}
/* Call to Action */
.cta-text {
  position: absolute;
  bottom: 60px;
  right: 80px;
  font-size: 32px;
  font-weight: 600;
  color: #FFFFFF;
  font-family: 'Poppins', sans-serif;
  text-decoration: underline;
  z-index: 10;
  white-space: nowrap;
}

${custom_css}
