components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Fredoka:wght@400;500;600;700&family=Inter:wght@400;500;600;700&display=swap');
* { margin: 0; padding: 0; box-sizing: border-box; }
html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  font-family: 'Inter', sans-serif;
  background: ${background_color};
  color: ${menu_text_color};
  overflow: hidden;
}
.menu-poster {
  width: 1080px;
  height: 1350px;
  padding: 90px 90px 0 160px;
  position: relative;
}
.header {
  position: absolute;
  top: 0;
  left: 0;
  width: 1080px;
  height: 120px;
  padding: 30px 40px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  z-index: 10;
}
.logo {
  height: 40px;
  width: auto;
}
.website-link {
  font-family: 'Inter', sans-serif;
  font-size: 24px;
  font-weight: 600;
  color: ${menu_text_color};
  text-decoration: none;
  letter-spacing: 0.5px;
}
.website-link:hover {
  text-decoration: underline;
}
.vertical-title {
  position: absolute;
  top: 180px;
  left: 30px;
  width: 180px;
  font-family: 'Fredoka', sans-serif;
  font-size: 96px;
  font-weight: 700;
  color: ${title_color};
  letter-spacing: 4px;
  text-transform: uppercase;
  writing-mode: vertical-rl;
  transform: rotate(180deg);
  line-height: 1;
  padding: 20px 0;
}
.menu-items {
  position: absolute;
  margin-left: 140px;
  display: flex;
  flex-direction: column;
  gap: 55px;
  top: 230px;
}
.menu-row {
  display: flex;
  align-items: flex-start;
  gap: 30px;
  position: relative;
}
.dish-photo {
  width: 380px;
  height: 220px;
  border-radius: 18px;
  object-fit: cover;
  filter: saturate(110%);
  box-shadow: 0 15px 25px rgba(0,0,0,0.15);
}
.photo-wrap {
  position: relative;
  display: flex;
  align-items: stretch;
  align-self: stretch;
}
.price-badge {
  position: absolute;
  right: -18px;
  bottom: -18px;
  min-width: 95px;
  min-height: 95px;
  padding: 16px 22px;
  background: ${badge_color};
  color: ${badge_text_color};
  font-family: 'Fredoka', sans-serif;
  font-size: 20px;
  font-weight: 700;
  border-radius: 999px;
  display: inline-flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  line-height: 1.15;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  box-shadow: 0 12px 18px rgba(0,0,0,0.18);
}
.price-badge span {
  display: block;
  font-size: 28px;
}
.menu-info {
  flex: 1;
  padding-top: 20px;
}
.menu-name {
  font-family: 'Fredoka', sans-serif;
  font-size: 30px;
  font-weight: 700;
  color: ${title_color};
  letter-spacing: 1px;
  text-transform: uppercase;
  margin-bottom: 10px;
}
.menu-desc {
  font-size: 20px;
  line-height: 1.4;
  max-width: 420px;
}
.cta-bar {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 1080px;
  height: 170px;
  background: ${cta_background_color};
  border-top-left-radius: 40px;
  border-top-right-radius: 40px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 120px 0 200px;
  color: ${cta_text_color};
  font-family: 'Fredoka', sans-serif;
  font-size: 28px;
}
.cta-label {
  font-size: 30px;
  text-transform: uppercase;
  margin-bottom: 8px;
  letter-spacing: 1px;
}
.cta-details {
  font-family: 'Inter', sans-serif;
  font-size: 26px;
  letter-spacing: 1px;
}
.contact {
  display: flex;
  flex-direction: column;
  gap: 10px;
}
${custom_css}
