components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  font-family: Arial, sans-serif;
  background: #000000;
}
.poster {
  width: 1080px;
  height: 1350px;
  background: ${background_color};
  color: ${primary_color};
  position: relative;
  overflow: hidden;
}

/* HEADER 7: Logo small in top-right corner (minimal) */
.header-7 {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  width: 100%;
  padding: 40px 60px;
  box-sizing: border-box;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 10;
}
.header-7 .logo {
  height: 50px;
  width: auto;
  opacity: 0.95;
}

/* BODY WRAPPER */
.content {
  position: absolute;
  top: 50%;
  left: 60px;
  width: calc(100% - 120px);
  transform: translateY(-50%);
}

.text1 {
  font-size: 90px;
  font-weight: 700;
  margin-bottom: 30px;
  line-height: 1.1;
  color: ${primary_color};
}

.text2 {
  font-size: 75px;
  font-weight: 600;
  margin-bottom: 60px;
  line-height: 1.1;
  color: ${primary_color};
}

/* IMAGE */
.image-holder img {
  width: 100%;
  height: 650px;
  object-fit: cover;
  border-radius: 32px;
  display: block;
}

/* FOOTER 9: Handle/website on left only */
.footer-9 {
  display: flex;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
  padding: 40px 60px;
  position: absolute;
  bottom: 0;
  left: 0;
  box-sizing: border-box;
}
.footer-9 .handle-website {
  font-size: 28px;
  font-weight: 500;
  color: ${accent_color};
  letter-spacing: 0.6px;
}

${custom_css}
