components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1080px;
  font-family: Arial, sans-serif;
  background: ${primary_color};
  color: white;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

/* HEADER */
.header-5 {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  padding: 40px 4%;
  box-sizing: border-box;
  flex-shrink: 0;
}

.header-5 .logo {
  height: clamp(25px, 3vh, 40px);
  width: auto;
}

.header-5 .handle-website {
  font-size: clamp(14px, 2vw, 22px);
  font-weight: 500;
  display: flex;
  align-items: center;
}

/* CONTENT CENTERING */
.content {
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 0 60px;
  box-sizing: border-box;
}

.subtitle {
  font-size: 42px;
  font-weight: 600;
  letter-spacing: 3px;
  margin-bottom: 60px;
}

.product {
  width: 100%;
  max-width: 900px;
  aspect-ratio: 16 / 9;
  object-fit: cover;
  border-radius: 20px;
  margin-bottom: 60px;
}

.cta {
  background: ${secondary_color};
  color: ${primary_color};
  padding: 26px 0;
  width: 80%;
  font-size: 50px;
  font-weight: 800;
}

${custom_css}
