components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  font-family: Arial, sans-serif;
  background: #000000;
}
.poster {
  width: 1080px;
  height: 1350px;
  background: ${background_color};
  color: ${primary_color};
  position: relative;
  overflow: hidden;
}
.content {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 100%;
  transform: translate(-50%, -50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

/* TITLE */
.title {
  font-size: 110px;
  font-weight: bold;
  line-height: 1.1;
  margin-bottom: 25px;
  color: ${primary_color};
  text-align: center;
}

/* SUBTITLE */
.subtitle {
  font-size: 38px;
  opacity: 0.9;
  margin-bottom: 90px;
  color: ${primary_color};
  text-align: center;
}

/* COLLAGE AREA */
.collage {
  width: 100%;
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  padding-bottom: 120px;
}

.polaroid {
  background: #fff;
  width: 360px;
  padding: 16px;
  padding-bottom: 50px;
  box-shadow: 0 8px 20px rgba(0,0,0,0.25);
  position: relative;
  margin-left: -35px;
  margin-right: -35px;
}

.polaroid:first-child {
  transform: rotate(-8deg) translateY(-15px) scale(0.98);
  margin-left: 0;
  z-index: 2;
  margin-top: 10px;
}

.polaroid:nth-child(2) {
  transform: rotate(12deg) translateY(20px) scale(1.02);
  z-index: 3;
  margin-top: -25px;
}

.polaroid:nth-child(3) {
  transform: rotate(-4deg) translateY(-5px) scale(0.96);
  z-index: 1;
  margin-right: 0;
  margin-top: 15px;
}

.polaroid img {
  width: 100%;
  aspect-ratio: 1 / 1;
  object-fit: cover;
  display: block;
}

/* Header 1: Logo in center */
.header-1 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 2% 4%;
  box-sizing: border-box;
  margin-bottom: 30px;
}
.header-1 .logo {
  height: clamp(30px, 5vh, 50px);
  width: auto;
  max-width: 200px;
}

/* Footer 1: Handle/website in center */
.footer-1 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 2% 4%;
  box-sizing: border-box;
  margin-top: 40px;
}
.footer-1 .handle-website {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
  font-size: clamp(14px, 2vw, 22px);
  font-weight: 500;
  color: ${primary_color};
  letter-spacing: 0.5px;
  text-align: center;
}

${custom_css}
