components.css_content:
/* Container page styles copied from original HTML (absolute layout preserved) */
* { box-sizing: border-box; }
html, body { margin: 0; padding: 0; }
body {
  margin: 0;
  padding: 0;
  display: flex;
  justify-content: center;
  background: rgb(221, 221, 221);
  width: 1080px;
  height: 1080px;
}

.post {
  width: 1080px;
  height: 1080px;
  background-color: ${background_color};
  position: relative;
  font-family: 'Arial', sans-serif;
}

/* Brand (Top-Right) */
.brand {
  position: absolute;
  top: 60px;
  right: 60px;
  font-size: 32px;
  color: ${text_color};
  font-weight: 500;
}

/* Title */
.title {
  position: absolute;
  top: 170px;
  width: 100%;
  text-align: center;
  font-size: 70px;
  font-weight: 400;
  letter-spacing: 3px;
  font-family: "Georgia", serif;
  color: ${text_color};
}

/* Description */
.description {
  position: absolute;
  top: 270px;
  width: 100%;
  text-align: center;
  font-size: 34px;
  font-weight: 400;
  color: ${text_color};
}

/* Product Image */
.product-wrapper {
  position: absolute;
  top: 360px;
  width: 100%;
  display: flex;
  justify-content: center;
}

.product-img {
  width: 620px;
}

/* Price Circle */
.price-tag {
  position: absolute;
  top: 800px;
  right: 300px;
  width: 150px;
  height: 150px;
  background: ${price_bg_color};
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  color: ${price_text_color};
  font-size: 48px;
  font-weight: 700;
}

/* Contact */
.contact {
  position: absolute;
  bottom: 20px;
  left: 60px;
  font-size: 36px;
  color: ${text_color};
}

.contact b {
  font-size: 32px;
}

/* Address */
.address {
  position: absolute;
  bottom: 20px;
  right: 70px;
  text-align: right;
  font-size: 32px;
  line-height: 42px;
  color: ${text_color};
}

/* Allow template-level CSS overrides */
${custom_css}
