components.css_content:
body {
    margin: 0;
    padding: 0;
    width: 1080px;
    height: 1080px;
    font-family: Arial, sans-serif;
    background: ${background_color};
    color: ${search_text_color};
    overflow: hidden;
    position: relative;
}

body::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('${background_image_url}');
    background-size: cover;
    background-position: center;
    opacity: ${background_overlay_opacity};
    z-index: 0;
}

.header,
.search-bar,
.footer {
    position: relative;
    z-index: 1;
}

.header {
    position: absolute;
    top: 200px;
    left: 50%;
    transform: translateX(-50%);
}

.header img {
    height: 40px;
    width: auto;
    display: block;
}

.search-bar {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 800px;
    height: 90px;
    background: ${search_bar_background};
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 30px;
    box-sizing: border-box;
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.15);
    backdrop-filter: blur(2px);
}

.left-section,
.right-section {
    display: flex;
    align-items: center;
    gap: 20px;
}

.search-text {
    font-size: 32px;
    font-weight: 500;
    color: ${search_text_color};
    white-space: pre-wrap;
}

svg {
    width: 32px;
    height: 32px;
    stroke: ${icon_stroke_color};
    stroke-width: 2.5;
    fill: none;
    flex-shrink: 0;
}

svg path {
    stroke: ${icon_stroke_color};
}

.footer {
    position: absolute;
    bottom: 200px;
    left: 50%;
    transform: translateX(-50%);
    font-size: 28px;
    color: ${footer_text_color};
    text-shadow: 0 4px 20px rgba(0, 0, 0, 0.4);
    font-weight: 500;
}

${custom_css}
