components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  font-family: Arial, sans-serif;
  background: ${background_color};
  position: relative;
  overflow: hidden;
  color: ${text_color};
  text-align: center;
}
.bg-img {
  position: absolute;
  width: 100%;
  height: 100%;
  background-image: url('${bg_image_url}');
  background-size: cover;
  background-position: center;
  opacity: ${bg_opacity};
  top: 0;
  left: 0;
  z-index: 0;
}
.content {
  position: relative;
  z-index: 2;
  width: 1080px;
  height: 1350px;
}
.header {
  position: absolute;
  top: 60px;
  left: 50%;
  transform: translateX(-50%);
  z-index: 3;
}
.header img {
  height: 50px;
}
.body-content {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.title {
  font-size: 70px;
  font-weight: bold;
  margin-bottom: 60px;
}
.options {
  display: flex;
  gap: 60px;
  justify-content: center;
}
.option-block {
  text-align: center;
  max-width: 400px;
}
.option-img {
  width: 100%;
  max-width: 380px;
  height: auto;
  aspect-ratio: 1;
  object-fit: cover;
  border-radius: 30px;
  background: #222;
}
.label {
  margin-top: 25px;
  font-size: 42px;
  font-weight: bold;
}
.footer {
  position: absolute;
  bottom: 50px;
  width: 100%;
  font-size: 32px;
  opacity: 0.9;
}
${custom_css}
