components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@500;600&family=Cormorant+Garamond:wght@700&family=Poppins:wght@400;500&display=swap');
* { margin:0; padding:0; box-sizing:border-box; }
body, html { height:100%; }
.container {
  width: 1080px;
  height: 1080px;
  position: relative;
  background: ${background_color};
  overflow: hidden;
  font-family: 'Poppins', sans-serif;
}
/* Decorative dots – slightly darker for contrast on cream */
.dots {
  position: absolute;
  font-size: 7px;
  color: #D4C2B0;
  line-height: 1;
  letter-spacing: 14px;
  opacity: 0.8;
}
.dots-top { top: 80px; right: 60px; }
.dots-left { top: 50%; left: 40px; transform: translateY(-50%) rotate(90deg); }
.dots-bottom { bottom: 100px; left: 60px; }
/* Brand */
.brand {
  position: absolute;
  top: 50px; left: 50%;
  transform: translateX(-50%);
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
  font-size: 28px;
  color: #1a1a1a;
}
/* Headings */
.heading {
  position: absolute;
  top: 140px; left: 50%;
  transform: translateX(-50%);
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
  font-size: 48px;
  color: #141414;
  text-align: center;
  width: 100%;
}
.title {
  position: absolute;
  top: 200px; left: 50%;
  transform: translateX(-50%);
  font-family: 'Cormorant Garamond', serif;
  font-weight: 700;
  font-size: 94px;
  line-height: 1.05;
  color: #141414;
  text-align: center;
  width: 100%;
}
/* Cards */
.card {
  position: absolute;
  width: 290px;
  height: 260px;
  background: white;
  border-radius: 28px;
  box-shadow: 0 15px 40px rgba(0,0,0,0.09);
  padding-top: 50px;
  text-align: center;
}
.num {
  position: absolute;
  top: -20px;
  left: 50%;
  transform: translateX(-50%);
  width: 80px;
  height: 56px;
  border-radius: 30px;
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
  font-size: 34px;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
}
.card-text {
  padding: 0 32px;
  font-size: 26px;
  line-height: 1.42;
  color: #1c1c1c;
  white-space: pre-wrap;
}
/* Card positions */
#c1 { top: 360px; left: 90px; }
#c2 { top: 360px; left: 400px; }
#c3 { top: 360px; left: 710px; }
#c4 { top: 680px; left: 90px; }
#c5 { top: 680px; left: 400px; }
#c6 { top: 680px; left: 710px; }
/* Number badge colors */
#c1 .num, #c4 .num { background: ${badge_color_1}; }
#c2 .num, #c5 .num { background: ${badge_color_2}; }
#c3 .num, #c6 .num { background: ${badge_color_3}; }
/* Footer */
.footer {
  position: absolute;
  bottom: 50px;
  left: 50%;
  transform: translateX(-50%);
  font-family: 'Montserrat', sans-serif;
  font-weight: 500;
  font-size: 26px;
  color: #333;
}

${custom_css}
