components.css_content:
* { margin: 0; padding: 0; box-sizing: border-box; }
body {
  width: 1080px;
  height: 1350px;
  font-family: Arial, sans-serif;
  overflow: hidden;
  color: #ffffff;
  position: relative;
  background-image: url('${background_image_url}');
  background-size: cover;
  background-position: center;
}
body::before {
  content: '';
  position: absolute;
  inset: 0;
  background: rgba(0,0,0,0.5);
  z-index: 1;
}
.header-6 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 40px 4%;
  position: relative;
  z-index: 2;
}
.header-6 .logo-section {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  margin-top: 20px;
}
.header-6 .logo {
  height: clamp(30px, 5vh, 50px);
  width: auto;
  max-width: 200px;
}
.header-6 .handle-website {
  font-size: clamp(12px, 1.8vw, 20px);
  font-weight: 500;
  letter-spacing: 0.5px;
}
.content {
  position: absolute;
  top: 180px;
  width: 100%;
  text-align: center;
  padding: 0 60px;
  box-sizing: border-box;
  z-index: 2;
  height: calc(100% - 180px);
  overflow: hidden;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
}
.title {
  font-size: 130px;
  font-weight: 600;
  line-height: 1;
  margin-bottom: 20px;
}
.title span {
  display: block;
  font-size: 120px;
  font-weight: 300;
  font-style: italic;
  margin-top: 10px;
}
.subtitle {
  margin-top: 40px;
  font-size: 44px;
  opacity: 0.9;
  margin-bottom: 60px;
}
.cta {
  background: #ffffff;
  color: #333333;
  padding: 25px 60px;
  font-size: 40px;
  border-radius: 6px;
  margin-bottom: 80px;
  display: inline-block;
}
.bottom-image {
  width: 50%;
  aspect-ratio: 3 / 4;
  object-fit: cover;
  border-radius: 12px;
  margin-top: -20px;
  max-height: calc(100% - 20px);
}
${custom_css}
