components.css_content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  margin: 0;
  padding: 0;
  width: 1080px;
  height: 1350px;
  overflow: hidden;
  font-family: Arial, sans-serif;
  position: relative;
  background-image: url('${background_image_url}');
  background-size: cover;
  background-position: center;
}

/* Dark overlay for better text readability */
body::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.7);
  z-index: 1;
}

/* Header 1: Logo in center */
.header-1 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 2% 4%;
  box-sizing: border-box;
  position: absolute;
  top: 140px;
  left: 0;
  z-index: 2;
}
.header-1 .logo {
  height: clamp(50px, 7vh, 80px);
  width: auto;
  max-width: 300px;
}

/* Content wrapper */
.content {
  text-align: center;
  width: 100%;
  position: absolute;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  color: #ffffff;
  padding: 0 60px;
  box-sizing: border-box;
  z-index: 2;
}

.preheading {
  display: inline-block;
  padding: 12px 24px;
  margin-bottom: 30px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 50px;
  font-size: 18px;
  font-weight: 500;
  color: #ffffff;
  letter-spacing: 0.3px;
  box-shadow: 0 8px 32px 0 rgba(0, 0, 0, 0.2);
}

.title {
  font-size: 64px;
  font-weight: bold;
  line-height: 1.2;
  margin-bottom: 20px;
}

.subtitle {
  font-size: 36px;
  opacity: 0.9;
}

/* Footer 1: Handle/website in center */
.footer-1 {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 2% 4%;
  box-sizing: border-box;
  position: absolute;
  bottom: 140px;
  left: 0;
  z-index: 2;
}
.footer-1 .handle-website {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
  font-size: clamp(14px, 2vw, 22px);
  font-weight: 500;
  color: #ffffff;
  letter-spacing: 0.5px;
  text-align: center;
}

${custom_css}
