components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Cormorant+Garamond:wght@300;400;500;600&display=swap');

@font-face {
  font-family: 'TheSeasons';
  src: url('TheSeasons.ttf') format('truetype');
}

@font-face {
  font-family: 'JimmyScript';
  src: url('JimmyScript.ttf') format('truetype');
}

body {
  margin: 0;
  padding: 0;
  background: #fff;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.post {
  width: 1080px;
  height: 1080px;
  background-image: url('${background_image_url}');
  background-size: cover;
  background-position: center;
  box-sizing: border-box;
  padding: 80px;
  text-align: center;
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
}

.text-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  flex: 1;
  margin: 0;
}

.logo {
  width: 200px;
  height: auto;
  margin-top: 20px;
  margin-bottom: 0;
  display: block;
}

.happy {
  font-family: 'TheSeasons', serif;
  font-size: 150px;
  color: ${accent_primary_color};
  font-weight: 400;
  margin-bottom: -60px;
}

.new-year {
  font-family: 'JimmyScript', cursive;
  font-size: 170px;
  color: ${accent_secondary_color};
  margin-top: -20px;
}

.message {
  font-family: 'Cormorant Garamond', serif;
  font-size: 36px;
  color: ${accent_tertiary_color};
  max-width: 80%;
  margin: 0 auto 50px auto;
  line-height: 1.4;
}

.website {
  font-family: 'Cormorant Garamond', serif;
  font-size: 32px;
  color: ${accent_tertiary_color};
  margin-top: 20px;
}

${custom_css}
