components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');
* { box-sizing: border-box; margin: 0; padding: 0; }
html,
body {
  width: 1080px;
  height: 1080px;
  font-family: "Inter", "Helvetica Neue", Arial, sans-serif;
  overflow: hidden;
}
.container {
  width: 1080px;
  height: 1080px;
  position: relative;
  background: url("https://i.ibb.co/j9c3TnmX/Blue-Modern-Search-Our-Services-Instagram-Post.png")
    center/cover no-repeat;
}

/* Service List Items */
.service-item {
  position: absolute;
  left: 330px;
  font-size: 32px;
  color: #182149;
  font-weight: 500;
  z-index: 10;
}

.item1 {
  top: 410px;
}

.item2 {
  top: 500px;
  background: transparent !important;
}

.item3 {
  top: 590px;
}

.item4 {
  top: 680px;
}

/* CTA button text */
.cta-text {
  position: absolute;
  bottom: 130px;
  left: 48%;
  transform: translateX(-50%);
  font-size: 22px;
  font-weight: 500;
  color: #ffffff;
  z-index: 10;
  white-space: nowrap;
}

${custom_css}
