components.css_content:
* { margin: 0; padding: 0; box-sizing: border-box; }
    body { font-family: 'Poppins', sans-serif; background: #fff; overflow: hidden; }
    .container { width: 1080px; height: 1350px; position: relative; background: linear-gradient(90deg, #1f3fcf 0%, #1a2a8a 100%); overflow: hidden; }
    .bg-top { position: absolute; top: 0; left: 0; width: 100%; height: 100%; background: linear-gradient(90deg, #1f3fcf 0%, #1a2a8a 100%); z-index: 1; }
    .bg-fade { position: absolute; top: 740px; left: 0; width: 100%; height: 610px; background: linear-gradient(270deg, rgba(31,63,207,0) 0%, rgba(31,63,207,0.87) 100%); z-index: 2; }
    .brand-name { position: absolute; top: 82px; left: 0; width: 100%; text-align: center; font-size: 48px; font-weight: 600; color: #ffffff; letter-spacing: 1.5px; z-index: 5; }
    .headline { position: absolute; top: 220px; left: 60px; width: 960px; font-size: 120px; font-weight: 800; color: #ffffff; text-align: center; line-height: 1; z-index: 5; }
    .tagline { position: absolute; top: 520px; left: 0; width: 100%; text-align: center; font-size: 44px; color: #e7ecff; font-weight: 400; line-height: 1.3; z-index: 5; }
    .hero-image { position: absolute; top: 740px; left: 0; width: 1080px; height: 610px; background: url('${hero_image_url}') center/cover no-repeat; z-index: 1; }
    .hero-tint { position: absolute; top: 0; left: 0; width: 100%; height: 100%; background: linear-gradient(180deg, rgba(31,63,207,0.86) 0%, rgba(31,63,207,0) 100%); z-index: 2; }
    .stats-box { position: absolute; top: 600px; left: 120px; width: 840px; height: 230px; background: linear-gradient(90deg, rgba(43,84,245,0.94) 0%, rgba(35,63,190,0.93) 100%); border: 4px solid #8aa0ff; border-radius: 40px; z-index: 6; display: flex; justify-content: space-around; align-items: center; padding: 0 20px; }
    .stat-item { text-align: center; flex: 1; }
    .stat-value { font-size: 84px; font-weight: 700; color: #f7b73f; margin-bottom: 8px; }
    .stat-label { font-size: 26px; color: #ffffff; font-weight: 400; line-height: 1.2; max-width: 230px; margin: 0 auto; }
    .cta-button { position: absolute; top: 860px; left: 330px; width: 420px; height: 108px; background: #f7b73f; border-radius: 60px; display: flex; align-items: center; justify-content: center; z-index: 7; }
    .cta-text { font-size: 40px; font-weight: 700; color: #1c2f8f; text-align: center; }
    .website { position: absolute; top: 1000px; left: 330px; width: 420px; text-align: center; font-size: 36px; color: #ffffff; font-weight: 400; z-index: 7; }
    ${custom_css}
