components.css_content:
@font-face {
  font-family: 'TheSeasons';
  src: url('TheSeasons.ttf') format('truetype');
}

body {
  margin: 0;
  padding: 0;
  background: #000;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.post {
  width: 1080px;
  height: 1080px;
  background-image: url('${background_image_url}');
  background-size: cover;
  background-position: center;
  box-sizing: border-box;
  padding: 60px;
  text-align: center;
  position: relative;
  color: ${accent_primary_color};
}

.content {
  margin-top: 250px;
  position: relative;
}

.logo {
  position: absolute;
  top: 60px;
  left: 50%;
  transform: translateX(-50%);
  height: 30px;
  width: auto;
  max-height: 120px;
  object-fit: contain;
  z-index: 10;
}

.happy {
  font-family: 'TheSeasons', serif;
  font-size: 120px;
  color: ${accent_primary_color};
  margin-top: 40px;
}

.new-year {
  font-family: 'TheSeasons', serif;
  font-size: 120px;
  color: ${accent_primary_color};
  margin-top: -20px;
}

.offer-strip {
  background: ${accent_secondary_color};
  color: ${accent_tertiary_color};
  font-family: 'TheSeasons', serif;
  font-size: 30px;
  width: fit-content;
  margin: 20px auto;
  padding: 10px 40px;
  border-radius: 4px;
}

.discount {
  font-family: 'TheSeasons', serif;
  font-size: 50px;
  color: ${accent_primary_color};
  margin-top: 20px;
}

${custom_css}
