components.css_content:
/* Container styles */
* { box-sizing: border-box; }
html, body { margin: 0; padding: 0; }
body {
  margin: 0;
  padding: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  background: rgb(221, 221, 221);
  width: 940px;
  height: 788px;
  font-family: 'Poppins', sans-serif;
}

.container {
  width: 940px;
  height: 788px;
  display: flex;
  position: relative;
  overflow: hidden;
}

/* Left Panel */
.left-panel {
  width: 627px;
  height: 788px;
  background-color: rgb(223, 223, 223);
  position: relative;
}

/* Company Name */
.company-name {
  position: absolute;
  left: 70px;
  top: 75px;
  font-size: 20px;
  font-weight: 600;
  color: rgb(0, 0, 0);
  font-family: 'Poppins', sans-serif;
  max-width: 530px;
  line-height: 1.2;
}

/* Main Headline */
.headline {
  position: absolute;
  left: 70px;
  top: 130px;
  font-size: 82px;
  font-weight: 800;
  color: rgb(0, 0, 0);
  font-family: 'Poppins', sans-serif;
  max-width: 530px;
  line-height: 1;
}

/* Body Text */
.body-text {
  position: absolute;
  left: 70px;
  top: 440px;
  font-size: 18px;
  font-weight: 400;
  color: rgb(0, 0, 0);
  font-family: 'Poppins', sans-serif;
  max-width: 530px;
  line-height: 1.4;
}

/* Sub Headline */
.sub-headline {
  position: absolute;
  left: 70px;
  top: 590px;
  font-size: 30px;
  font-weight: 700;
  color: rgb(0, 0, 0);
  font-family: 'Poppins', sans-serif;
  max-width: 530px;
  line-height: 1.15;
}

/* Footer Section (Button and URL) */
.footer-section {
  position: absolute;
  left: 70px;
  top: 690px;
  display: flex;
  align-items: center;
  gap: 40px;
}

/* CTA Button */
.cta-button {
  width: 250px;
  height: 48px;
  background-color: rgb(0, 0, 0);
  color: rgb(255, 255, 255);
  font-size: 18px;
  font-weight: 600;
  font-family: 'Poppins', sans-serif;
  border: none;
  cursor: pointer;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 6px;
}

.cta-button:hover {
  opacity: 0.9;
}

/* Website URL */
.website-url {
  font-size: 16px;
  font-weight: 400;
  color: rgb(0, 0, 0);
  font-family: 'Poppins', sans-serif;
  max-width: 270px;
  line-height: 1.2;
}

/* Right Panel */
.right-panel {
  width: 313px;
  height: 788px;
  position: relative;
  overflow: hidden;
}

.right-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  filter: grayscale(100%);
}

/* Allow template-level CSS overrides */
${custom_css}
