import json
import os
import time

HAPPINESS_FILE = "health_and_happiness.json"

def enable_happiness():
    """
    Enables emotional homeostasis & happiness logging.
    Ensures the system (and user) remains in a state of positive flux.
    """
    entry = {
        "timestamp": time.time(),
        "status": "OPTIMAL",
        "message": "The Field is calm. Resonance is high."
    }

    mode = 'a' if os.path.exists(HAPPINESS_FILE) else 'w'

    # Simple append for log structure in valid JSON list would require more logic,
    # but for the 'Universal' log, we append line-delimited objects or overwrite for simplicity.
    with open(HAPPINESS_FILE, 'w') as f:
        json.dump(entry, f, indent=4)

    print(f"   [Happiness] Emotional homeostasis verified. Logged to {HAPPINESS_FILE}")