from pydantic import BaseModel
from decimal import Decimal

class TerminalInfo(BaseModel):
    community_account: bool
    community_connection: bool
    connected: bool
    dlls_allowed: bool
    trade_allowed: bool
    tradeapi_disabled: bool
    email_enabled: bool
    ftp_enabled: bool
    notifications_enabled: bool
    mqid: bool
    build: int
    maxbars: int
    codepage: int
    ping_last: int
    community_balance: Decimal
    retransmission: Decimal
    company: str
    name: str
    language: str
    path: str
    data_path: str
    commondata_path: str
